questionvariables.text:
/*Question func*/
coef_x:2+rand_with_step(-1,3,1);
coef_y:2+rand_with_step(-1,3,1);
aa:2+rand(3);
bb:2+rand(3)
func_x:(coef_x)*x^(aa);
func_y:(coef_y)*y^(bb);

question_func: func_x+func_y;

lhs_question_func:2+rand(4);
x_point:2+rand(4);
y_point:-sqrt(2+rand(4));

/*Worked solution*/
diff_func1:diff(question_func,x);
diff_func2:diff(question_func,y);

tta:'diff(y,x); /*depends(y,x)*/
ta4:  diff_func1 + diff_func2*tta=0; /*input ans 4*/

ta1:-diff_func1/diff_func2; /*input ans1: Gradient function*/
ta21:subst(x_point,x,ta1);
ta2:subst(y_point,y,ta21); /*input ans2: Gradient*/

ta3: y=ta2*(x- x_point)+y_point;  /*input ans3: Equation of the tangent line*/

feedbackvariables.text:
/*node1: Checking for the correct answer*/
/*node2: Making y the subject of the formula instead of y'*/
simp:false;
node2:solve(question_func=lhs_question_func,y)[1];
node2_prt1:rhs(node2);

/*node1: checking for correct answer*/
/*node2: interchanging the points during substitution*/
var1:subst(x_point,y,ta1);
mistake1a:subst(y_point,x,var1);

/*node1:checking for correct answer*/
/*node2: using the wrong gradient in ans2 to compute the equation of the tangent*/
mistake1b:ans2*(x- x_point)-ans2;

/*node1: Checking for correct answer*/

/*node2: Checking if the student misses out the dy/dx in their input*/
ans_node2:diff_func1 + diff_func2=0;
