questionvariables.text:
/*integration by parts cyclic*/
trig:rand([sin,cos]);
power: rand(3)+1;
quest_expr:%e^(power*x)*trig(x);
/*worked solution variables*/
/*choosing the values of u and dv*/
u:%e^(power*x);
du:diff(u,x);
dv:trig(x);
v:int(dv,x);
uv_1:u*v;
vdu_1:v*du;
/*applying integration by parts for the second time*/
u_2:diff(u,x);
du_2:diff(du,x);
v_2:diff(v,x);
dv_2:int(dv,x);
u2_v2:u_2*v_2;
v_du_2:v_2*du_2;
/*model ansswer*/
ta1:int(quest_expr,x)+c;

feedbackvariables.text:
/*Node 1: Checks for the correct answer */
/*Node 2: Forgetting the constant of integration*/
ta1_cste:int(quest_expr,x);
