questionvariables.text:
mean1:rand(101)+100;
aa:rand([-1,0,1]);
sd1:rand(5)+5;
bb:rand([1/3,1/2,1,2,3]);
ds1:rand(41)+60;
dun1:makelist(round(float(quantile_normal(rand(1.0),mean1,sd1))),i,1,ds1);
dorx1:sort(dun1);
m1:float(std(dorx1));
mr1:dispdp(m1,1);
mean_real: round(mean(dorx1));

maxfreq1:0;
k:1;
for i:2 thru ds1 do if dorx1[i]=dorx1[i-1] then (k:k+1, if maxfreq1 < k then maxfreq1:k) else k:1;

xmin:dorx1[1]-2;
xmax:dorx1[ds1]+2+(dorx1[ds1]-dorx1[1])/1.5;
ymin:-2;
ymax:if maxfreq1+2<15 then 15 else maxfreq1+2;

j:1;
dory:makelist(0,i,1,ds1);
dory[1]:1;
for i:2 thru ds1 do if dorx1[i]=dorx1[i-1] then (j:j+1, dory[i]:j) else (j:1, dory[i]:j);
